package TEST;

public class PYRAMID_TEST {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int i,j,k;
		for(i=0;i<5;i++) {
			for(j=i;j<=5;j++) {
				System.out.print(" ");
			}
			
				for(k=0;k<=i;k++) {
					
			System.out.print(" *");
			
				}
				System.out.println();			
	
		}
	}
				
}
